function returnColourNameDayTime(tempCelcius) {
    if(tempCelcius >= 25) return "red";
    else if(tempCelcius < 25 && tempCelcius >= 19) return "green";
    else if(tempCelcius < 19) return "teal"
    else return "gold";
}

function returnColourNameNightTime(tempCelcius) {
    if(tempCelcius >= 22) return "red";
    else if(tempCelcius < 22 && tempCelcius >= 17) return "green";
    else if(tempCelcius < 17) return "teal"
    else return "gold";
}

function returnIconID(colourName) {
    var result = "";
    switch (colourName) {
    case "teal":
        result = "24568"
        break;
    case "green":
        result = "8044"
        break;
    case "red":
        result = "19651"
        break;
    default:
        result = "8044"
    }
    return result;
}

