let usHeatingOn = false;
let dsHeatingOn = false;
let usCoolingOn = false;
let dsCoolingOn = false;
let cooling = false;
let heating = false;
let serviceData = {}
let debugThis = false;
let isOn = false;
msg.callService = false;
let mode = msg.hvac_mode_payload;


function HeatOperation() {
    if (msg.ds_switch_payload === "on" && parseFloat(msg.ds_targettemp_payload) > parseFloat(msg.ds_temp_payload)) {
        dsHeatingOn = true;
    } else if (msg.ds_switch_payload === "on" && parseFloat(msg.ds_targettemp_payload) <= parseFloat(msg.ds_temp_payload)) {
        dsHeatingOn = false;
    }

    if (msg.us_switch_payload === "on" && parseFloat(msg.us_targettemp_payload) > parseFloat(msg.us_temp_payload)) {
        usHeatingOn = true;
    } else if (msg.us_switch_payload === "on" && parseFloat(msg.us_targettemp_payload) <= parseFloat(msg.us_temp_payload)) {
        usHeatingOn = false;
    }

    if (IsOn()) {
        serviceData = {
            data: {
                raw_command: {
                    "system":"on",
                    "type":"heat", 
                    "mode":"thermo"
                }
            }
        };

        if (dsHeatingOn && usHeatingOn) {
            serviceData.data.raw_command.zoneList = "1,2";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.ds_targettemp_payload > msg.us_targettemp_payload ? msg.ds_targettemp_payload : msg.us_targettemp_payload);
        } else if (dsHeatingOn && !usHeatingOn) {
            serviceData.data.raw_command.zoneList = "2";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.ds_targettemp_payload);
        } else if (!dsHeatingOn && usHeatingOn) {
            serviceData.data.raw_command.zoneList = "1";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.us_targettemp_payload);
        } else if (!dsHeatingOn && !usHeatingOn) {
            serviceData.data.raw_command.system = "off";
            serviceData.data.raw_command.zoneList = "1,2";
        }
        msg.serviceData = serviceData;
        msg.payload = serviceData;
    }
    if (debugThis)
        node.warn(msg.serviceData.data.raw_command)

}

function CoolOperation() {
    if (msg.ds_switch_payload === "on" && parseFloat(msg.ds_targettemp_payload) < parseFloat(msg.ds_temp_payload)) {
        dsCoolingOn = true;
    } else if (msg.ds_switch_payload === "on" && parseFloat(msg.ds_targettemp_payload) >= parseFloat(msg.ds_temp_payload)) {
        dsCoolingOn = false;
    }

    if (msg.us_switch_payload === "on" && parseFloat(msg.us_targettemp_payload) < parseFloat(msg.us_temp_payload)) {
        usCoolingOn = true;
    } else if (msg.us_switch_payload === "on" && parseFloat(msg.us_targettemp_payload) <= parseFloat(msg.us_temp_payload)) {
        usCoolingOn = false;
    }

    if (IsOn()) {
        serviceData = {
            data: {
                raw_command: {
                    "system": "on",
                    "type": "cool",
                    "mode": "thermo"
                }
            }
        };

        if (dsCoolingOn && usCoolingOn) {
            serviceData.data.raw_command.zoneList = "1,2";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.ds_targettemp_payload > msg.us_targettemp_payload ? msg.ds_targettemp_payload : msg.us_targettemp_payload);
        } else if (dsCoolingOn && !usCoolingOn) {
            serviceData.data.raw_command.zoneList = "2";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.ds_targettemp_payload);
        } else if (!dsCoolingOn && usCoolingOn) {
            serviceData.data.raw_command.zoneList = "1";
            serviceData.data.raw_command.setPoint = Math.trunc(msg.us_targettemp_payload);
        } else if (!dsCoolingOn && !usCoolingOn) {
