            serviceData.data.raw_command.zoneList = "1,2";
        }
        msg.serviceData = serviceData;
        msg.payload = serviceData;
    }
    if (debugThis)
        node.warn(msg.serviceData.data.raw_command)

}


function IsOn() {
    return (msg.ds_switch_payload === "on" || msg.us_switch_payload === "on");
}

function TurnOff() {
    serviceData = {
        data: {
            raw_command: {
                "system": "on",
                "type": "heat",
                "mode": "thermo"
            }
        }
    };
    serviceData.data.raw_command.system = "off";
    serviceData.data.raw_command.zoneList = "1,2";
    msg.serviceData = serviceData;
    msg.payload = serviceData;
}

if (IsOn()) {
    if (mode === "Heat") {
        HeatOperation();
    }
    else if (mode === "Cool") {
        CoolOperation();
    }
} else {
    TurnOff();
}

